import {Component} from '@angular/core';
import {Articulo} from "../../models/Articulo";
import {ActivatedRoute} from "@angular/router";
import {ArticuloService} from '../../services/articulo.service';
import {CarritoService} from "../../services/carrito.service";

@Component({
  selector: 'app-detalle-articulo',
  templateUrl: './detalle-articulo.component.html',
  styleUrls: ['./detalle-articulo.component.css']
})
export class DetalleArticuloComponent {

  articuloCategoria: string | null = "";
  articulo: Articulo = new Articulo();
  cantidad: number = 1;

  constructor(
    private route: ActivatedRoute,
    private articuloService: ArticuloService,
    private carritoService: CarritoService
  ) {
  }

  ngOnInit(): void {
    this.articuloCategoria = this.route.snapshot.paramMap.get('articulo');

    if (this.articuloCategoria) {

      const [categoria, modelo, color, talla] = this.articuloCategoria.split('-');

      this.articuloService.MostrarArticuloPorId().subscribe(datos => {
        for (let a of datos) {
          if (a.categoria === categoria && a.modelo === modelo && a.color === color && a.talla === talla) {
            this.articulo = a;
            break;
          }
        }
      });
    }
  }

  alCarrito(articulo: Articulo): void {
    articulo.cantidad = this.cantidad;
    this.carritoService.agregarAlCarrito(articulo);
    alert('Agregado al carrito!');
  }

}
<div class="detalle-container">
  <img src="{{ articulo.url_Imagen }}" alt="">
  <div class="detalle-item">
    <p>{{ articulo.categoria }} {{ articulo.modelo }}</p>
    <p> COLOR: {{ articulo.color }}</p>
    <p>{{ articulo.talla }}</p>
    <p>{{ articulo.modelo }}</p>
    <form (ngSubmit)="alCarrito(articulo)">
      <input type="number" name="cantidad" min="1" [defaultValue]="1" class="select-input" [(ngModel)]="cantidad">
      <br>
      <button type="submit">Agregar al carrito</button>
    </form>
    <a routerLink="/">
      <svg style="position: relative; top: 2px;" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-arrow-left" viewBox="0 0 16 16">
        <path fill-rule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"/>
      </svg>
      Volver a la lista de articulos
    </a>
  </div>
</div>
