import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {Articulo} from "../../models/Articulo";
import {CarritoService} from "../../services/carrito.service";


@Component({
  selector: 'app-articulo',
  templateUrl: './articulo.component.html',
  styleUrls: ['./articulo.component.css']
})
export class ArticuloComponent {

  cantidad: number = 1;
  @Input() articulo: Articulo;
  @Output() agregar = new EventEmitter;

  constructor(private carritoService: CarritoService) {
    this.articulo = {
      iD_Articulo: 0,
      categoria: "",
      color: "",
      talla: "",
      modelo: "",
      precio: 0.0,
      stock: 0,
      cantidad: 0,
      url_Imagen: ""
    }
  }

  Agregar(articulo: Articulo): void {
    articulo.cantidad = this.cantidad;
    this.carritoService.agregarAlCarrito(articulo);
  }

}
<div class="articulo-container">
  <a [routerLink]="'/' + articulo.modelo"><img
    src="{{ articulo.url_Imagen }}" alt="" class="articulo-image"></a>
  <p>{{ articulo.categoria }} {{ articulo.modelo }}</p>
  <p>{{ articulo.precio }} €</p>
  <form (ngSubmit)="Agregar(articulo)">
    <input type="number" name="amount" min="1" [defaultValue]="1" class="select-input" [(ngModel)]="cantidad">
    <br>
    <button type="submit" (click)="agregar.emit()">Agregar al carrito</button>
  </form>
</div>
