import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { Articulo } from 'src\app\services\articulo.service.ts';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-articulo',
  templateUrl: './articulo.component.html',
  styleUrls: ['./articulo.component.css']
})
export class ArticuloComponent implements OnInit {
  articulo: Articulo | null = null;
  error: string | null = null;

  constructor(private apiService: ApiService, private route: ActivatedRoute) { }

  ngOnInit(): void {
    const id = +this.route.snapshot.paramMap.get('id')!; // Obtiene el ID del parámetro de la ruta
    this.apiService.getArticuloPorID(id).subscribe({
      next: (data) => {
        this.articulo = data;
      },
      error: (err) => {
        this.error = 'Error al cargar el artículo';
        console.error(err);
      }
    });
  }
}
