import { Component } from '@angular/core';
import {InfoService} from "../../info.service";

@Component({
  selector: 'app-confirmacion',
  templateUrl: './confirmacion.component.html',
  styleUrls: ['./confirmacion.component.css']
})
export class ConfirmacionComponent {

  nombre: string = '';
  total: number = 0;

  constructor(private infoService: InfoService) {}

  ngOnInit(): void {
    this.nombre = this.infoService.nombre;
    this.total = this.infoService.total;
  }

}
<div class="confirmacion-container">
  <h1 class="confirmacion-header">EXITO :)</h1>
  <div class="confirmacion-items">
    <p class="confirmacion-item">Gracias, <b>{{ nombre }}</b>!</p>
    <p class="confirmacion-item">Tu Pedido de <b> {{ total }} €</b> ha sido confirmado</p>
    <p class="confirmacion-item">Has sido estafado... :O </p>
  </div>
  <button routerLink="/">Volver a la lista de productos</button>
</div>
